import React from 'react';
import { useRouter } from 'next/router'


export default () => {
  const router = useRouter()
    //this id will be matching the [id] from the file name
  //params will be an array /notes/1/2/3
  const { id }= router.query

  return (
    <div>
      <h1>Note: {id} </h1>

      <Link href="/notes">
        <a>Notes</a>
      </Link>
    </div>
  )
}
